import { createContext, useContext, useState, useEffect, type ReactNode } from 'react';
import { supabase, isConfigured } from '../lib/supabaseClient';

interface User {
    id: string;
    email: string;
    name: string;
    avatar?: string;
    role: 'photographer' | 'visitor' | 'admin' | 'client';
}

interface AuthContextType {
    user: User | null;
    isAuthenticated: boolean;
    login: (email: string, password: string) => Promise<void>;
    signup: (email: string, password: string, name: string, role?: 'photographer' | 'visitor' | 'admin' | 'client', metadata?: Record<string, unknown>) => Promise<void>;
    logout: () => void;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

const MOCK_USERS_KEY = 'tunisian_lens_mock_users';
const CURRENT_USER_KEY = 'tunisian_lens_current_user';

// Seed a default admin account so it's always available
function seedAdminAccount() {
    const mockUsers = JSON.parse(localStorage.getItem(MOCK_USERS_KEY) || '[]');
    const adminExists = mockUsers.some((u: { email: string }) => u.email === 'admin@tunisianlens.com');
    if (!adminExists) {
        mockUsers.push({
            id: 'admin-1',
            email: 'admin@tunisianlens.com',
            password: 'admin123',
            name: 'Site Admin',
            role: 'admin'
        });
        localStorage.setItem(MOCK_USERS_KEY, JSON.stringify(mockUsers));
        console.log("ðŸ”‘ Default admin account seeded: admin@tunisianlens.com / admin123");
    }
}
seedAdminAccount();

export function AuthProvider({ children }: { children: ReactNode }) {
    const [user, setUser] = useState<User | null>(null);

    interface SupabaseUser { id: string; email?: string; user_metadata: Record<string, any>; }
    const fetchUserProfile = async (supabaseUser: SupabaseUser) => {
        try {
            // 1. Get user role and verification status from public.users
            let { data: profile, error: profileError } = await supabase
                .from('users')
                .select('*')
                .eq('id', supabaseUser.id)
                .single();

            if (profileError && profileError.code !== 'PGRST116') {
                console.error("Profile fetch error:", profileError);
            }

            // If profile doesn't exist (happens right after signup), the trigger might not have finished
            // or we might need to create it manually if triggers are disabled.
            if (profileError && profileError.code === 'PGRST116') {
                const { data: newProfile, error: _createError } = await supabase
                    .from('users')
                    .upsert({
                        id: supabaseUser.id,
                        email: supabaseUser.email,
                        name: supabaseUser.user_metadata.full_name || supabaseUser.email?.split('@')[0],
                        role: supabaseUser.user_metadata.role || 'photographer'
                    })
                    .select()
                    .single();

                if (!_createError) profile = newProfile;
            }

            // 2. If photographer, ensure photographer record exists
            if (profile?.role === 'photographer') {
                const { data: photo, error: _photoError } = await supabase
                    .from('photographers')
                    .select('*')
                    .eq('user_id', supabaseUser.id)
                    .single();

                if (_photoError && _photoError.code === 'PGRST116') {
                    // Create photographer profile
                    await supabase.from('photographers').insert({
                        user_id: supabaseUser.id,
                        location: supabaseUser.user_metadata.city || '',
                        connects_balance: 0 // Start at 0 until verified
                    });
                }

                // 3. Connects Award Logic: If verified and never awarded, give 20 connects
                if (profile.is_verified) {
                    const { data: transactions } = await supabase
                        .from('connect_transactions')
                        .select('id')
                        .eq('photographer_id', photo?.id || '')
                        .eq('reason', 'bonus')
                        .limit(1);

                    if (!transactions || transactions.length === 0) {
                        // Get photo id if we just created it or it was missing
                        const photoId = photo?.id || (await supabase.from('photographers').select('id').eq('user_id', supabaseUser.id).single()).data?.id;

                        if (photoId) {
                            // Award 20 connects
                            await supabase.from('photographers').update({ connects_balance: 20 }).eq('id', photoId);
                            await supabase.from('connect_transactions').insert({
                                photographer_id: photoId,
                                amount: 20,
                                reason: 'bonus'
                            });
                        }
                    }
                }
            }

            // Map data
            const userData: User = {
                id: supabaseUser.id,
                email: supabaseUser.email || '',
                name: profile?.name || supabaseUser.user_metadata.full_name || supabaseUser.email?.split('@')[0] || 'User',
                avatar: supabaseUser.user_metadata.avatar_url || "https://randomuser.me/api/portraits/lego/1.jpg",
                role: profile?.role || supabaseUser.user_metadata.role || 'photographer'
            };

            setUser(userData);
        } catch (_e) {
            console.error("Failed to fetch extended profile:", _e);
        }
    };

    // Sync auth state
    useEffect(() => {
        if (isConfigured) {
            // Get initial Supabase session
            supabase.auth.getSession().then(({ data: { session } }) => {
                if (session?.user) {
                    fetchUserProfile(session.user);
                }
            });

            // Listen for Supabase auth changes
            const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
                if (session?.user) {
                    fetchUserProfile(session.user);
                } else {
                    setUser(null);
                }
            });

            return () => subscription.unsubscribe();
        } else {
            // Mock mode: Check localStorage for session
            const storedUser = localStorage.getItem(CURRENT_USER_KEY);
            if (storedUser) {
                try {
                    setUser(JSON.parse(storedUser));
                } catch (_e) {
                    localStorage.removeItem(CURRENT_USER_KEY);
                }
            }
            console.log("ðŸ› ï¸ Auth running in Mock Mode (No Supabase detected)");
        }
    }, []);

    const login = async (email: string, password: string) => {
        if (isConfigured) {
            const { error } = await supabase.auth.signInWithPassword({
                email,
                password,
            });
            if (error) throw error;
        } else {
            // Mock login
            const mockUsers = JSON.parse(localStorage.getItem(MOCK_USERS_KEY) || '[]');
            const foundUser = mockUsers.find((u: { email: string; password: string }) => u.email === email && u.password === password);

            if (foundUser) {
                const userData: User = {
                    id: foundUser.id,
                    email: foundUser.email,
                    name: foundUser.name,
                    avatar: "https://randomuser.me/api/portraits/lego/1.jpg",
                    role: foundUser.role || 'photographer'
                };
                setUser(userData);
                localStorage.setItem(CURRENT_USER_KEY, JSON.stringify(userData));
            } else {
                throw new Error("Invalid email or password");
            }
        }
    };

    const signup = async (email: string, password: string, name: string, role: 'photographer' | 'visitor' | 'admin' | 'client' = 'photographer', metadata?: Record<string, unknown>) => {
        if (isConfigured) {
            const { error } = await supabase.auth.signUp({
                email,
                password,
                options: {
                    data: {
                        full_name: name,
                        role: role,
                        ...metadata
                    },
                },
            });
            if (error) throw error;
        } else {
            // Mock signup
            const mockUsers = JSON.parse(localStorage.getItem(MOCK_USERS_KEY) || '[]');
            if (mockUsers.some((u: { email: string }) => u.email === email)) {
                throw new Error("User already exists");
            }

            const newUser = {
                id: Math.random().toString(36).substr(2, 9),
                email,
                password,
                name,
                role,
                ...metadata
            };

            mockUsers.push(newUser);
            localStorage.setItem(MOCK_USERS_KEY, JSON.stringify(mockUsers));

            const userData: User = {
                id: newUser.id,
                email: newUser.email,
                name: newUser.name,
                avatar: "https://randomuser.me/api/portraits/lego/1.jpg",
                role: newUser.role as 'photographer' | 'visitor' | 'admin' | 'client'
            };
            setUser(userData);
            localStorage.setItem(CURRENT_USER_KEY, JSON.stringify(userData));
        }
    };

    const logout = async () => {
        if (isConfigured) {
            await supabase.auth.signOut();
        } else {
            localStorage.removeItem(CURRENT_USER_KEY);
            setUser(null);
        }
    };

    return (
        <AuthContext.Provider value={{ user, isAuthenticated: !!user, login, signup, logout }}>
            {children}
        </AuthContext.Provider>
    );
}

export function useAuth() {
    const context = useContext(AuthContext);
    if (context === undefined) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
}

import React, { createContext, useContext, useState, useEffect, useCallback } from 'react';
import { supabase, isConfigured } from '../lib/supabaseClient';
import { type Job, type JobApplication, MOCK_JOBS } from '../data/mockData';
import { useAuth } from './AuthContext';

const INITIAL_CONNECTS = 20;

interface MarketplaceContextType {
    jobs: Job[];
    applications: JobApplication[];
    isLoading: boolean;
    postJob: (data: Omit<Job, 'id' | 'createdAt' | 'applicantCount' | 'clientId' | 'clientName' | 'status'>) => Promise<void>;
    applyToJob: (jobId: string, coverLetter: string, proposedPrice: number) => Promise<{ success: boolean; message: string }>;
    getJobApplications: (jobId: string) => JobApplication[];
    getMyApplications: () => JobApplication[];
    getConnects: (userId: string) => number;
    hasApplied: (jobId: string) => boolean;
    closeJob: (jobId: string) => Promise<void>;
    refreshData: () => Promise<void>;
}

const MarketplaceContext = createContext<MarketplaceContextType | undefined>(undefined);

export const MarketplaceProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
    const { user } = useAuth();
    const [jobs, setJobs] = useState<Job[]>([]);
    const [applications, setApplications] = useState<JobApplication[]>([]);
    const [connectsBalance, setConnectsBalance] = useState<Record<string, number>>({});
    const [isLoading, setIsLoading] = useState(true);

    const refreshData = useCallback(async () => {
        if (!isConfigured) {
            setJobs(MOCK_JOBS);
            setIsLoading(false);
            return;
        }

        try {
            setIsLoading(true);

            // 1. Fetch Jobs
            const { data: jobsData, error: jobsError } = await supabase
                .from('jobs')
                .select('*, client:users(name)')
                .order('created_at', { ascending: false });

            if (jobsError) throw jobsError;

            // 2. Fetch Proposals (Applications)
            const { data: proposalsData, error: propsError } = await supabase
                .from('proposals')
                .select('*, job:jobs(title), photographer:photographers(user_id, users(name, avatar_url))');

            if (propsError) throw propsError;

            // 3. Fetch Photographers (for connects)
            const { data: photoData, error: photoError } = await supabase
                .from('photographers')
                .select('user_id, connects_balance');

            if (photoError) throw photoError;

            // Map Jobs
            const mappedJobs: Job[] = jobsData.map(j => ({
                id: j.id,
                title: j.title,
                description: j.description || '',
                budget: j.budget,
                currency: 'USD',
                location: j.location || '',
                isRemote: !j.location,
                category: j.category || 'Other',
                deadline: j.deadline || '',
                connectsRequired: j.connects_required,
                clientId: j.client_id,
                clientName: j.client?.name || 'Client',
                status: j.status,
                createdAt: j.created_at,
                applicantCount: proposalsData.filter(p => p.job_id === j.id).length
            }));

            // Map Applications
            const mappedApps: JobApplication[] = proposalsData.map(p => ({
                id: p.id,
                jobId: p.job_id,
                photographerId: p.photographer?.user_id,
                photographerName: p.photographer?.users?.name || 'Photographer',
                photographerAvatar: p.photographer?.users?.avatar_url || "https://randomuser.me/api/portraits/lego/1.jpg",
                coverLetter: p.cover_letter || '',
                proposedPrice: p.proposed_price,
                currency: 'USD',
                status: p.status,
                createdAt: p.created_at
            }));

            // Map Connects
            const balanceMap: Record<string, number> = {};
            photoData.forEach(p => {
                balanceMap[p.user_id] = p.connects_balance;
            });

            setJobs(mappedJobs);
            setApplications(mappedApps);
            setConnectsBalance(balanceMap);

        } catch (_secret_e) {
            console.error("Error refreshing marketplace data:", _secret_e);
        } finally {
            setIsLoading(false);
        }
    }, []);

    useEffect(() => {
        refreshData();
    }, [refreshData]);

    const postJob = async (data: Omit<Job, 'id' | 'createdAt' | 'applicantCount' | 'clientId' | 'clientName' | 'status'>) => {
        if (!user || !isConfigured) return;

        const { error } = await supabase.from('jobs').insert({
            client_id: user.id,
            title: data.title,
            description: data.description,
            budget: data.budget,
            category: data.category,
            location: data.location,
            deadline: data.deadline,
            connects_required: data.connectsRequired
        });

        if (error) throw error;
        await refreshData();
    };

    const applyToJob = async (jobId: string, coverLetter: string, proposedPrice: number): Promise<{ success: boolean; message: string }> => {
        if (!user || !isConfigured) return { success: false, message: 'Supabase not configured or user not logged in.' };
        if (user.role !== 'photographer') return { success: false, message: 'Only photographers can apply.' };

        try {
            // 1. Get photographer record
            const { data: photo, error: photoError } = await supabase
                .from('photographers')
                .select('id, connects_balance, is_verified:users(is_verified)')
                .eq('user_id', user.id)
                .single();

            if (photoError) throw photoError;

            const isVerifiedResult = photo.is_verified as unknown as { is_verified: boolean } | { is_verified: boolean }[];
            const isVerified = Array.isArray(isVerifiedResult)
                ? isVerifiedResult[0]?.is_verified
                : isVerifiedResult?.is_verified;

            if (!isVerified) return { success: false, message: 'You must be verified to apply.' };

            // 2. Get Job
            const job = jobs.find(j => j.id === jobId);
            if (!job) return { success: false, message: 'Job not found.' };

            if (photo.connects_balance < job.connectsRequired) {
                return { success: false, message: 'Insufficient connects.' };
            }

            // 3. Create Proposal & Deduct Connects (Transaction-like)
            // In a real app, this should be a DB function or trigger to be atomic.
            const { error: propError } = await supabase.from('proposals').insert({
                job_id: jobId,
                photographer_id: photo.id,
                cover_letter: coverLetter,
                proposed_price: proposedPrice
            });

            if (propError) {
                if (propError.code === '23505') return { success: false, message: 'You have already applied.' };
                throw propError;
            }

            // 4. Update Connects
            const { error: updateError } = await supabase
                .from('photographers')
                .update({ connects_balance: photo.connects_balance - job.connectsRequired })
                .eq('id', photo.id);

            if (updateError) throw updateError;

            // 5. Log Transaction
            await supabase.from('connect_transactions').insert({
                photographer_id: photo.id,
                amount: -job.connectsRequired,
                reason: 'job_apply'
            });

            await refreshData();
            return { success: true, message: 'Application sent!' };

        } catch (e: unknown) {
            console.error("Application error:", e);
            return { success: false, message: (e as Error).message || 'Failed to submit application.' };
        }
    };

    const getJobApplications = (jobId: string) => applications.filter(a => a.jobId === jobId);
    const getMyApplications = () => applications.filter(a => a.photographerId === user?.id);
    const getConnects = (userId: string) => connectsBalance[userId] ?? INITIAL_CONNECTS;
    const hasApplied = (jobId: string) => applications.some(a => a.jobId === jobId && a.photographerId === user?.id);

    const closeJob = async (jobId: string) => {
        if (!isConfigured) return;
        await supabase.from('jobs').update({ status: 'closed' }).eq('id', jobId);
        await refreshData();
    };

    return (
        <MarketplaceContext.Provider value={{
            jobs, applications, isLoading,
            postJob, applyToJob,
            getJobApplications, getMyApplications,
            getConnects, hasApplied, closeJob,
            refreshData
        }}>
            {children}
        </MarketplaceContext.Provider>
    );
};

export function useMarketplace() {
    const ctx = useContext(MarketplaceContext);
    if (!ctx) throw new Error('useMarketplace must be used within MarketplaceProvider');
    return ctx;
}

import React, { createContext, useContext, useState, useEffect } from 'react';
import { supabase, isConfigured } from '../lib/supabaseClient';
import { useAuth } from './AuthContext';

export interface Artist {
    id: string;
    name: string;
    avatar: string;
    location: string;
    bio?: string;
    tags?: string[];
    followers?: number;
    following?: number;
    projectCount?: number;
}

export interface Project {
    id: string;
    title: string;
    image: string;
    category: string;
    likes: number;
    artist: {
        id: string;
        name: string;
        avatar: string;
    };
    description?: string;
    tags?: string[];
    date?: string;
    isPrivate?: boolean;
    isDownloadable?: boolean;
    accessCode?: string;
}

interface ProjectContextType {
    projects: Project[];
    publicProjects: Project[];
    isLoading: boolean;
    addProject: (newProjectData: Omit<Project, 'id' | 'likes' | 'artist'>) => Promise<void>;
    getProject: (id: string) => Project | undefined;
    getProjectByCode: (code: string) => Project | undefined;
    deleteProject: (id: string) => Promise<void>;
}

const ProjectContext = createContext<ProjectContextType | undefined>(undefined);

export const ProjectProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
    const [projects, setProjects] = useState<Project[]>([]);
    const [isLoading, setIsLoading] = useState(true);
    const { user } = useAuth();

    // Derived state for public projects
    const publicProjects = projects.filter(p => !p.isPrivate);

    // Fetch projects â€” from Supabase if configured, otherwise from localStorage only
    const fetchProjects = async () => {
        setIsLoading(true);
        try {
            if (isConfigured) {
                const { data, error } = await supabase
                    .from('projects')
                    .select('*')
                    .order('created_at', { ascending: false });

                if (error) {
                    console.warn('Supabase fetch error, using localStorage fallback', error);
                    const stored = localStorage.getItem('tunisian_lens_projects_v2');
                    const parsed: any[] = stored ? JSON.parse(stored) : [];
                    setProjects(parsed.map((p: any) => ({
                        ...p,
                        image: p.image || p.image_url || ''
                    })));
                } else if (data && data.length > 0) {
                    setProjects(data.map((p: any) => ({
                        ...p,
                        image: p.image || p.image_url,
                        isPrivate: p.isPrivate ?? p.is_private,
                        isDownloadable: p.isDownloadable ?? p.is_downloadable,
                        accessCode: p.accessCode ?? p.access_code
                    })));
                } else {
                    setProjects([]);
                }
            } else {
                // No Supabase â€” use localStorage submissions only (no mock data)
                const stored = localStorage.getItem('tunisian_lens_projects_v2');
                const parsed: any[] = stored ? JSON.parse(stored) : [];
                setProjects(parsed.map((p: any) => ({
                    ...p,
                    image: p.image || p.image_url || ''
                })));
            }
        } catch (e) {
            console.warn('Project fetch failed:', e);
            setProjects([]);
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        fetchProjects();
    }, []);

    const addProject = async (newProjectData: Omit<Project, 'id' | 'likes' | 'artist'>) => {
        if (!user) return;

        const newProject = {
            title: newProjectData.title,
            category: newProjectData.category,
            image: newProjectData.image,
            description: newProjectData.description,
            tags: newProjectData.tags,
            is_private: newProjectData.isPrivate,
            is_downloadable: newProjectData.isDownloadable,
            access_code: newProjectData.accessCode,
            likes: 0,
            artist: {
                id: user.id,
                name: user.name,
                avatar: user.avatar
            }
        };

        const { data, error } = await supabase
            .from('projects')
            .insert([newProject])
            .select();

        if (error) {
            console.error("Error adding project to Supabase:", error);
            // Fallback for demo
            const localProject: Project = {
                id: Date.now().toString(),
                ...newProject,
                artist: newProject.artist as any,
                date: new Date().toISOString()
            } as any;
            const updated = [localProject, ...projects];
            setProjects(updated);
            localStorage.setItem('tunisian_lens_projects_v2', JSON.stringify(updated));
            return;
        }

        if (data) {
            setProjects([data[0], ...projects]);
        }
    };

    const getProject = (id: string) => {
        return projects.find(p => p.id === id);
    };

    const getProjectByCode = (code: string) => {
        return projects.find(p => p.accessCode === code);
    };

    const deleteProject = async (id: string) => {
        const { error } = await supabase
            .from('projects')
            .delete()
            .eq('id', id);

        if (error) {
            console.error("Error deleting project from Supabase:", error);
            // Fallback for local
            const updatedProjects = projects.filter(p => p.id !== id);
            setProjects(updatedProjects);
            localStorage.setItem('tunisian_lens_projects_v2', JSON.stringify(updatedProjects));
            return;
        }

        setProjects(projects.filter(p => p.id !== id));
    };

    return (
        <ProjectContext.Provider value={{ projects, publicProjects, isLoading, addProject, getProject, getProjectByCode, deleteProject }}>
            {children}
        </ProjectContext.Provider>
    );
}

export function useProjects() {
    const context = useContext(ProjectContext);
    if (context === undefined) {
        throw new Error('useProjects must be used within a ProjectProvider');
    }
    return context;
}

